mapper.select:
SELECT *
FROM my_stock
WHERE user_id = #{userId}
  AND strategy_name LIKE '%_KR'
  AND deleted_yn = 'N'
ORDER BY created_at DESC
    LIMIT #{page.limit}
OFFSET #{page.offset}

SELECT *
FROM my_stock
WHERE user_id = #{userId}
  AND strategy_name LIKE '%_US'
  AND deleted_yn = 'N'
ORDER BY created_at DESC
    LIMIT #{page.limit}
OFFSET #{page.offset}

SELECT *
FROM my_stock
WHERE user_id = #{userId}
  AND code = #{code}
  AND deleted_yn = 'N'
    LIMIT 1

SELECT COUNT(1)
FROM my_stock
WHERE user_id = #{userId}
  AND code = #{code}
  AND deleted_yn = 'N'

mapper.update:
UPDATE my_stock
SET deleted_yn = 'Y',
    deleted_at = NOW()
WHERE id = #{id}
  AND user_id = #{userId}

UPDATE my_stock
SET deleted_yn = 'N',
    deleted_at = NULL
WHERE id = #{id}
  AND user_id = #{userId}

mapper.select:
SELECT *
FROM my_stock
WHERE user_id = #{userId}
  AND deleted_yn = 'Y'
ORDER BY deleted_at DESC
    LIMIT #{page.limit}
OFFSET #{page.offset}
